sources.scss2/helper/global.scss:
body {
  font-family: $font-family;
  line-height: 1.4;
}

h1, h2, h3 {
  line-height: 1.2;
}

.container {
  max-width: $max-width;
  margin-left: auto;
  margin-right: auto;
  padding-left: $gutter;
  padding-right: $gutter;
}

a {
  transition: .15s;
}

.icon {
  display: inline-block;
  width: 24px;
  height: 24px;
  stroke-width: 0;
  stroke: currentColor;
  fill: currentColor;
}

.select select, .button, .text {
  font-family: inherit;
  border-radius: $border-radius;
  border: 0;
  font-weight: bold;
  background-color: #fff;
}

.button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding-left: 20px;
  padding-right: 20px;
  text-align: center;
  height: $finger-size;
  color: white;
  background-color: $color-blue;

  &-icon {
    justify-content: center;
    color: black;
    padding-left: 0;
    padding-right: 0;
    width: $finger-size;
    background-color: transparent;
  }

  &-full {
    width: 100%;
  }

  &-navy {
    background-color: $color-navy;
  }
}

.text {
  min-height: $finger-size;
  border-radius: $border-radius;
  color: $color-navy;
  padding-left: 10px;
  padding-right: 10px;
  width: 100%;

  &::placeholder {
    color: rgba($color-navy, .5);
  }
}

.select {
  position: relative;
  color: $color-navy;

  select {
    appearance: none;
    display: inline-block;
    width: 100%;
    padding-right: 30px;
    color: $color-navy;
    height: $finger-size;
  }

  &-icon {
    pointer-events: none;
    z-index: 1;
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
  }
}

.form-item {
  position: relative;

  .form-item-icon {
    pointer-events: none;
    z-index: 1;
    position: absolute;
    left: 10px;
    top: 50%;
    transform: translateY(-50%);
    color: $color-navy;
  }
  .text {
    padding-left: 40px;
  }

  .select select {
    padding-left: 40px;
  }
}

.form-description {
  margin-top: 5px;
  font-size: $font-size-small;
  @include mq("tablet") {
    margin-top: 20px;
  }
}

.form-checkbox {
  display: flex;
  align-items: center;
}

.checkbox {
  position: relative;
  display: flex;

  input {
    position: absolute;
    opacity: 0;
    top: 0;
    left: 0;
    pointer-events: none;
  }

  &:before {
    content: "";
    display: inline-block;
    font-size: 18px;
    width: 1em;
    height: 1em;
    border: 2px solid white;
  }

  .icon {
    display: none;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    margin-left: 3px;
    margin-top: -2px;
    width: 20px;
    height: 20px;
  }

  input:checked + .icon {
    display: block;
  }

}

sources.scss2/config/mixin.scss:
@mixin mq($width: "tablet") {
  @if ($width == "tablet") {
    // Medium devices (tablets, 768px and up)
    @media (min-width: 768px) {
      @content
    }
  } @else if ($width == "desktop") {
    // Large devices (desktops, 992px and up)
    @media (min-width: 992px) {
      @content
    }
  } @else if ($width == "large") {
    // Extra large devices (large desktops, 1200px and up)
    @media (min-width: 1200px) {
      @content
    }
  }
}

sources.scss2/global/header.scss:
$Header-height: 68px;

.Header {
  height: $Header-height;

  .container {
    position: relative;
    display: flex;
    align-items: center;
    height: 100%;
  }

  &-logo {
    img {
      width: 144px;
    }
  }

  &-switch {
    position: relative;
    left: 10px;
    margin-left: auto;
    @include mq {
      display: none;
    }
  }

  &-menu {
    pointer-events: none;
    opacity: 0;
    transition: .2s;
    display: flex;
    position: fixed;
    left: 0;
    right: 0;
    top: $Header-height;
    padding-left: $gutter;
    padding-right: $gutter;
    padding-bottom: 20px;
    flex-direction: column;
    background-color: white;
    box-shadow: 0 20px 30px rgba(black, .2);

    .Header.menu-show & {
      z-index: 2;
      pointer-events: auto;
      opacity: 1;
      //transform: translateY(0);
    }

    @include mq("tablet") {
      pointer-events: auto;
      opacity: 1;
      //transform: translateY(0);
      transition: 0s;
      margin-left: auto;
      flex-direction: row;
      position: static;
      box-shadow: none;
      background-color: transparent;
      padding-left: 0;
      padding-right: 0;
      padding-bottom: 0;
    }

    &-user {
      margin-right: 10px;
      display: flex;
      flex-direction: column;
      align-items: center;
      @include mq("tablet") {
        flex-direction: row;
      }

      a {
        width: 100%;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        padding-left: 20px;
        padding-right: 20px;
        height: $finger-size;
        &:hover {
          background-color: #eee;
        }
      }
    }

    &-property {
      margin-top: 20px;
      justify-content: center;
      @include mq("tablet") {
        margin-top: 0;
      }
    }

    &-helper {
      margin-top: 20px;
      display: flex;
      justify-content: center;

      @include mq("tablet") {
        margin-left: 20px;
        margin-top: 0;
      }

      select {
        appearance: none;
        background-color: transparent;
        border: 1px solid #E0E6F1;
        padding-left: 10px;
        padding-right: 10px;
        border-radius: 0;
        color: $color-navy;
        height: $finger-size;

        &:nth-child(1) {
          border-bottom-left-radius: $border-radius;
          border-top-left-radius: $border-radius;
        }
        &:nth-child(2) {
          border-left-width: 0;
          border-bottom-right-radius: $border-radius;
          border-top-right-radius: $border-radius;
        }
      }

    }
  }
}

