.Showcase {
  color: white;
  padding-top: 30px;
  padding-bottom: 30px;
  background-color: $color-navy ;
  text-align: center;

  @include mq("tablet") {
    padding-top: 80px;
    padding-bottom: 170px;
  }

  &-title {
    @include mq("tablet") {
      font-size: 3em;
    }
  }

  &-subtitle {
    margin-top: 10px;
    @include mq("tablet") {
      font-size: 1.4em;
    }
  }

  &-shortlink {
    margin-top: 40px;
    @include mq("tablet") {
      margin-top: 60px;
      display: flex;
      align-items: center;
      justify-content: center;
    }

    a {
      display: block;
      color: inherit;
      @include mq("tablet") {
        margin-left: 10px;
        margin-right: 10px;
      }

      & + a {
        margin-top: 10px;
        @include mq("tablet") {
          margin-top: 0;
        }
      }

      &:hover {
        text-decoration: underline;
      }
    }
  }

  &-store {
    margin-top: 40px;
    display: flex;
    align-items: center;
    justify-content: center;

    &-link {
      display: flex;
      align-items: center;
      justify-content: center;
      height: 45px;
      padding-left: 20px;
      padding-right: 20px;
      background-color: rgba(black, .15);
      margin-left: 10px;
      margin-right: 10px;
      border-radius: $border-radius;
      @include mq("tablet") {
        height: 50px;
      }

      &:hover {
        background-color: rgba(black, .3);
      }

