  .Footer .bottom-left-items {
    width: calc(33.3333333333% - 16px);
  }
}
@media (min-width: 992px) {
  .Footer .bottom-left-items {
    width: calc(20% - 19.2px);
  }
}
.Footer .bottom-right {
  display: flex;
  flex-direction: column;
}
.Footer .bottom-right-icons {
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  gap: 20px;
}
.Footer .bottom-right-icons-link {
  padding: 15px;
  border-radius: 3px;
  background-color: #083b87;
  flex-basis: 50%;
  max-width: 160px;
}
.Footer .bottom-right-icons-link img {
  width: 100px;
}
.Footer .bottom-right .hr {
  margin-top: 20px;
}
.Footer .bottom-right-info p {
  opacity: 1;
  font-size: 0.95rem;
  margin-bottom: 30px;
}
@media (min-width: 992px) {
  .Footer .bottom-right-info p {
    font-size: 0.94rem;
  }
}
.Footer .bottom-right-info a {
  text-decoration: underline;
  opacity: 1;
}
.Footer .copyright {
  font-size: 0.95rem;
  opacity: 0.6;
  margin-bottom: 10px;
  text-align: center;
}
@media (min-width: 880px) {
  .Footer .copyright {
    margin-top: 100px;
  }
}

