sources.scss/helper/global.scss:
body {
  font-family: $font-family;
  line-height: 1.4;
}

h1,
h2,
h3 {
  line-height: 1.2;
}

.container {
  width: 90%;
  margin-left: auto;
  margin-right: auto;
  @include mq('large') {
    max-width: $max-width;
  }
}

a {
  transition: 0.2s;
}

.button {
  font-family: inherit;
  display: inline-flex;
  align-items: center;
  text-align: center;
  justify-content: center;
  padding-left: $gutter;
  padding-right: $gutter;
  height: $finger-size;
  color: white;
  background-color: $color-blue;
  border-radius: $border-radius;
  font-weight: bold;
  transition: background-color 0.3s;
  cursor: pointer;
  &:hover {
    background-color: lighten($color: $color-blue, $amount: 5);
  }
  &-icon {
    justify-content: center;
    color: black;
    padding-left: 0;
    padding-right: 0;
    width: $finger-size;
    cursor: pointer;
    background-color: transparent;
    &:hover {
      background-color: transparent;
    }
  }
  &-full {
    width: 100%;
  }
  &-navy {
    background-color: $color-navy;
    &:hover {
      background-color: lighten($color: $color-navy, $amount: 10);
    }
  }
}

.icon {
  display: inline-block;
  width: 24px;
  height: 24px;
  stroke-width: 0;
  stroke: currentColor;
  fill: currentColor;
}

.text {
  font-family: inherit;
  border: 0;
  border-radius: $border-radius;
  color: $color-navy;
  min-height: $finger-size;
  font-weight: bold;
  padding-left: 10px;
  padding-right: 10px;
}

.form-item {
  position: relative;
  display: flex;
  flex-direction: column;

  .icon {
    pointer-events: none;
    position: absolute;
    left: 10px;
    top: 50%;
    transform: translateY(-50%);
    color: $color-navy;
  }
  .text {
    padding-left: 40px;
    &::placeholder {
      color: rgba($color-navy, .8);
    }
  }
}

.form-description {
  display: block;
  margin-top: 8px;
  @include mq("tablet") {
    font-size: $font-size-small;
    margin-top: 16px;
  }
}

.form-checkbox {
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  @include mq("tablet") {
    justify-content: flex-end;
    font-size: $font-size-small;
  }
}

.checkbox {
  display: flex;
  position: relative;
  input {
    position: absolute;
    opacity: 0;
    pointer-events: none;
  }
  &:before {
    content: "";
    display: inline-block;
    font-size: 18px;
    width: 1em;
    height: 1em;
    border: 2px solid white;
    border-radius: 2px;
  }
  .icon {
    display: none;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    width: 20px;
    height: 20px;
    margin-left: 2px;
    margin-top: -2px;
  }
  input:checked + .icon {
    display: inline-block;
  }
}

input[type="date"]::-webkit-calendar-picker-indicator {
  opacity: 0;
  position: absolute;
  left: -20px;
  z-index: 999;
  width: 30px;
  height: 30px;
}

.index-title {
  text-align: center;
  margin-top: 30px;
  margin-bottom: 30px;
  color: $color-navy;
  @include mq('tablet') {
    font-size: 40px;
    margin-bottom: 50px;
  }
}

.icon-right {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  right: 10px;
}

.hr {
  width: 100%;
  height: 1px;
  opacity: .3;
  margin-bottom: 30px;
}

.Onecıkan {
  overflow: hidden;
  margin-bottom: 30px;
  margin-top: 30px;
  padding-bottom: 20px;
  @include mq('tablet') {
    margin-top: -100px;
  }
  &-kayma {
    .flickity-viewport {
      overflow: visible;
    }
  }
  &-item {
    width: 80%;
    height: 200px;
    background: white center center no-repeat / cover;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    padding: 20px;
    color: white;

    &:before {
      content: "";
      position: absolute;
      left: 0;
      top: 0;
      width: 100%;
      height: 100%;
    }

    .icerik {
      z-index: 1;
      .baslık {
        margin-bottom: 8px;
      }
      .tag {
        &-baslık {
          opacity: 0.7;
          font-weight: 400;
          cursor: auto;
        }
        &-item {
          font-size: $font-size-medium;
          &:not(:last-child){
            margin-right: 10px;
            &:after {
              position: absolute;
              content: ',';
            }
          }
        }
      }
      .fiyat-etiketi {
        width: 70px;
        background-color: $color-yellow;
        position: absolute;
        font-size: 0.8rem;
        font-weight: 700;
        top: 20px;
        right: -10px;
        padding: 3px;
        box-shadow: 0 5px 10px rgba(black, 0.3);
        &:after {
        }
      }
    }
  }
  &-extra {
    display: none;
    width: 100%;
    .baslık {
      color: $color-navy;
      margin-bottom: 8px;
    }
    .icon {
      opacity: 0.6;
      width: 10px;
      height: 10px;
      margin-right: 5px;
    }
    span {
      font-size: $font-size-small;
      position: relative;
      &.date {
        font-weight: bold;
        margin-right: 5px;
        opacity: .9;
      }
      &.details {
        &:before {
          content: '/ ';
        }
      }
    }
    .button-details {
      margin-top: 15px;
      display: flex;
      width: max-content;
    }
  }
  &.is-expanded {
    height: 400px;

    @include mq('tablet') {
      height: 200px;
      width: 630px;
      flex-direction: row;
    }
    .Onecıkan-item-show,
    .Onecıkan-item-show:before {
      height: 50%;
      @include mq('tablet') {
        width: 50%;
        height: 100%;
      }
    }
    .fiyat-etiketi {
      display: none;
    }
    .Onecıkan-item-extra {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: flex-start;
      padding: 20px;
      @include mq('tablet') {
        width: 50%;
      }
    }
  }
}

sources.scss/config/mixin.scss:
@mixin mq($width: "tablet") {
  @if ($width == "tablet") {
    // Medium devices (tablets, 768px and up)
    @media (min-width: 880px) {
      @content;
    }
  } @else if($width == "desktop") {
    // Large devices (desktops, 992px and up)
    @media (min-width: 992px) {
      @content;
    }
  } @else if($width == "large") {
    // Extra large devices (large desktops, 1200px and up)
    @media (min-width: 1200px) {
      @content;
    }
  }
}

sources.scss/plugin/flickity.scss:
/*! Flickity v2.2.2
https://flickity.metafizzy.co
---------------------------------------------- */

.flickity-enabled {
  position: relative;
}

.flickity-enabled:focus { outline: none; }

.flickity-viewport {
  overflow: hidden;
  position: relative;
  height: 100%;
}

.flickity-slider {
  position: absolute;
  width: 100%;
  height: 100%;
}

/* draggable */

.flickity-enabled.is-draggable {
  -webkit-tap-highlight-color: transparent;
  -webkit-user-select: none;
     -moz-user-select: none;
      -ms-user-select: none;
          user-select: none;
}

.flickity-enabled.is-draggable .flickity-viewport {
  cursor: move;
  cursor: -webkit-grab;
  cursor: grab;
}

.flickity-enabled.is-draggable .flickity-viewport.is-pointer-down {
  cursor: -webkit-grabbing;
  cursor: grabbing;
}

/* ---- flickity-button ---- */

.flickity-button {
  position: absolute;
  background: hsla(0, 0%, 100%, 0.75);
  border: none;
  color: #333;
}

.flickity-button:hover {
  background: white;
  cursor: pointer;
}

.flickity-button:focus {
  outline: none;
  box-shadow: 0 0 0 5px #19F;
}

.flickity-button:active {
  opacity: 0.6;
}

.flickity-button:disabled {
  opacity: 0.3;
  cursor: auto;
  /* prevent disabled button from capturing pointer up event. #716 */
  pointer-events: none;
}

.flickity-button-icon {
  fill: currentColor;
}

/* ---- previous/next buttons ---- */

.flickity-prev-next-button {
  top: 50%;
  width: 44px;
  height: 44px;
  border-radius: 50%;
  /* vertically center */
  transform: translateY(-50%);
}

.flickity-prev-next-button.previous { left: 10px; }
.flickity-prev-next-button.next { right: 10px; }
/* right to left */
.flickity-rtl .flickity-prev-next-button.previous {
  left: auto;
  right: 10px;
}
.flickity-rtl .flickity-prev-next-button.next {
  right: auto;
  left: 10px;
}

.flickity-prev-next-button .flickity-button-icon {
  position: absolute;
  left: 20%;
  top: 20%;
  width: 60%;
  height: 60%;
}

/* ---- page dots ---- */

.flickity-page-dots {
  position: absolute;
  width: 100%;
  bottom: -25px;
  padding: 0;
  margin: 0;
  list-style: none;
  text-align: center;
  line-height: 1;
}

.flickity-rtl .flickity-page-dots { direction: rtl; }

.flickity-page-dots .dot {
  display: inline-block;
  width: 10px;
  height: 10px;
  margin: 0 8px;
  background: #333;
  border-radius: 50%;
  opacity: 0.25;
  cursor: pointer;
}

.flickity-page-dots .dot.is-selected {
  opacity: 1;
}

sources.scss/global/header.scss:
$Header-height: 68px;

.Header {
  height: $Header-height;
  position: sticky;
  top: 0;
  background-color: white;
  z-index: 2;


  .container {
    position: relative;
    display: flex;
    align-items: center;
    height: 100%;
    .Header.container-show & {
      pointer-events: auto;
      opacity: 1;
    }
  }

  &-logo {
    img {
      width: 144px;
    }
  }

  &-switch {
    position: relative;
    left: 10px;
    margin-left: auto;
    @include mq {
      display: none;
    }
  }

  &-menu {
    pointer-events: none;
    opacity: 0;
    transition: .2s;
    display: flex;
    position: fixed;
    left: 0;
    right: 0;
    top: $Header-height;
    padding-left: $gutter;
    padding-right: $gutter;
    padding-bottom: 20px;
    flex-direction: column;
    background-color: white;
    box-shadow: 0 20px 30px rgba(black, .2);

    .Header.menu-show & {
      pointer-events: auto;
      opacity: 1;
    }

    @include mq("tablet") {
      pointer-events: auto;
      opacity: 1;
      transition: 0s;
      margin-left: auto;
      flex-direction: row;
      position: static;
      box-shadow: none;
      background-color: transparent;
      padding-left: 0;
      padding-right: 0;
      padding-bottom: 0;
    }

    &-user {
      margin-right: 10px;
      display: flex;
      flex-direction: column;
      align-items: center;
      @include mq("tablet") {
        flex-direction: row;
      }

      a {
        width: 100%;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        padding-left: 20px;
        padding-right: 20px;
        height: $finger-size;
        &:hover {
          background-color: #eee;
        }
      }
    }

    &-property {
      margin-top: 20px;
      justify-content: center;
      @include mq("tablet") {
        margin-top: 0;
      }
    }

    &-helper {
      margin-top: 20px;
      display: flex;
      justify-content: center;

      @include mq("tablet") {
        margin-left: 20px;
        margin-top: 0;
      }

      a {
        appearance: none;
        background-color: transparent;
        border: 1px solid #E0E6F1;
        padding-left: 10px;
        padding-right: 10px;
        border-radius: 0;
        color: $color-navy;
        height: $finger-size;
        display: inline-flex;
        align-items: center;
        justify-content: center;

        &:visited {
          background-color: $color-blue;
        }

        &:nth-child(1) {
          border-bottom-left-radius: $border-radius;
          border-top-left-radius: $border-radius;
        }
        &:nth-child(2) {
          border-left-width: 0;
          border-bottom-right-radius: $border-radius;
          border-top-right-radius: $border-radius;
        }
      }

    }
  }
}

